ON CONFLICT DO NOTHING;
EOF

echo "✅ Sample embeddings table created with test data"

# Display database information
echo ""
echo "📊 Database Setup Complete!"
echo ""
echo "🗄️  Database Information:"
echo "   Main Database: $DB_NAME"
echo "   Test Database: $DB_TEST_NAME"
echo "   User: $DB_USER"
echo "   Host: localhost"
echo "   Port: 5432"
echo ""
echo "🧩 Installed Extensions:"
echo "   ✅ vector (pgvector for embeddings)"
echo "   ✅ uuid-ossp (UUID generation)"
echo "   ✅ pg_trgm (text similarity)"
echo "   ✅ btree_gin (GIN indexes)"
echo "   ✅ pg_stat_statements (query statistics)"
echo ""
echo "📊 Sample Tables:"
echo "   ✅ sample_embeddings (with vector similarity search)"
echo ""
echo "💡 Connection string for applications:"
echo "   postgresql://$DB_USER:$DB_PASSWORD@localhost:5432/$DB_NAME"
echo ""
echo "🔗 Access via Adminer: http://localhost:8080"
echo "   Server: localhost"
echo "   Username: $DB_USER"
echo "   Password: $DB_PASSWORD"
echo "   Database: $DB_NAME"
echo ""